import React from 'react';
import { Link } from 'react-router-dom';

const Cart = ({ cart, setCart }) => {
  const totalCost = cart.reduce((acc, product) => acc + product.price, 0);

  // Function to remove product from the cart
  const removeFromCart = (productId) => {
    setCart(cart.filter(product => product.id !== productId));
  };

  return (
    <div className="p-4">
      <h2 className="text-2xl mb-4">Your Cart</h2>
      {cart.length === 0 ? (
        <p>Your cart is empty</p>
      ) : (
        <div>
          <ul>
            {cart.map((product) => (
              <li key={product.id} className="flex justify-between mb-2">
                <span>{product.title}</span>
                <span>${product.price}</span>
                <button
                  onClick={() => removeFromCart(product.id)}
                  className="bg-red-500 text-white p-1 rounded"
                >
                  Remove
                </button>
              </li>
            ))}
          </ul>
          <div className="mt-4">
            <p className="font-bold">Total: ${totalCost}</p>
            <Link to="/checkout" className="bg-blue-500 text-white p-2 mt-4 inline-block">
              Proceed to Checkout
            </Link>
          </div>
        </div>
      )}
    </div>
  );
};

export default Cart;
