import React, { useState, useEffect } from 'react';
import { Route, Routes } from 'react-router-dom';
import ProductList from './components/ProductList';
import Cart from './components/Cart';
import Checkout from './components/checkout';
import Header from './components/Header';

const App = () => {
  const [products, setProducts] = useState([]);
  const [cart, setCart] = useState([]);

  useEffect(() => {
    // Mock product data, you can replace this with API calls
    setProducts([
      { id: 1, title: 'Product 1', description: 'Description 1', price: 20, image: '/images/product1.jpg' },
      { id: 2, title: 'Product 2', description: 'Description 2', price: 50, image: '/images/product2.jpg' },
      { id: 3, title: 'Product 3', description: 'Description 3', price: 30, image: '/images/product3.jpg' },
      { id: 4, title: 'Product 4', description: 'Description 4', price: 40, image: '/images/product4.jpg' },
      { id: 5, title: 'Product 5', description: 'Description 5', price: 60, image: '/images/product5.jpg' },
      { id: 6, title: 'Product 6', description: 'Description 6', price: 25, image: '/images/product6.jpg' },
      { id: 7, title: 'Product 7', description: 'Description 7', price: 35, image: '/images/product7.jpg' },
      { id: 8, title: 'Product 8', description: 'Description 8', price: 45, image: '/images/product8.jpg' },
      { id: 9, title: 'Product 9', description: 'Description 9', price: 55, image: '/images/product9.jpg' },
      { id: 10, title: 'Product 10', description: 'Description 10', price: 65, image: '/images/product10.jpg' },
      { id: 11, title: 'Product 11', description: 'Description 11', price: 70, image: '/images/product11.jpg' },
      { id: 12, title: 'Product 12', description: 'Description 12', price: 80, image: '/images/product12.jpg' },
      { id: 13, title: 'Product 13', description: 'Description 13', price: 90, image: '/images/product13.jpg' },
      { id: 14, title: 'Product 14', description: 'Description 14', price: 100, image: '/images/product14.jpg' },
      { id: 15, title: 'Product 15', description: 'Description 15', price: 110, image: '/images/product15.jpg' },
    ]);
  }, []);

  const addToCart = (product) => {
    setCart([...cart, product]);
  };

  return (
    <div>
      <Header cart={cart} />
      <Routes>
        <Route path="/" element={<ProductList products={products} addToCart={addToCart} />} />
        <Route path="/cart" element={<Cart cart={cart} setCart={setCart} />} />
        <Route path="/checkout" element={<Checkout />} />
      </Routes>
    </div>
  );
};

export default App;
