import React from 'react';

const ProductList = ({ products, addToCart }) => {
  return (
    <div className="product-list grid grid-cols-3 gap-6 p-8">
      {products.map((product) => (
        <div
          key={product.id}
          className={`card bg-white shadow-lg rounded-lg p-4 transform transition-all duration-300 ${
            product.price > 50 ? 'card-large' : ''
          }`}
        >
          <img src={product.image} alt={product.title} className="w-full h-48 object-cover mb-4 rounded-md" />
          <h3 className="text-xl font-bold mb-2">{product.title}</h3>
          <p className="text-gray-700 mb-4">{product.description}</p>
          <p className="text-lg font-semibold text-green-600">${product.price}</p>
          <button
            onClick={() => addToCart(product)}
            className="mt-4 w-full bg-blue-500 text-white p-2 rounded-lg hover:bg-blue-600 transition"
          >
            Add to Cart
          </button>
        </div>
      ))}
    </div>
  );
};

export default ProductList;
