import React, { useState, useEffect } from 'react';
import { Route, Routes } from 'react-router-dom';
import ProductList from './components/ProductList';
import Cart from './components/Cart';
import Checkout from './components/Checkout';
import Header from './components/Header';

const App = () => {
  const [products, setProducts] = useState([]);
  const [cart, setCart] = useState([]);

  useEffect(() => {
    // Mock product data, you can replace this with API calls
    setProducts([
      { id: 1, title: 'Product 1', description: 'Description 1', price: 20, image: 'product1.jpg' },
      { id: 2, title: 'Product 2', description: 'Description 2', price: 50, image: 'product2.jpg' },
      { id: 3, title: 'Product 3', description: 'Description 3', price: 30, image: 'product3.jpg' },
    ]);
  }, []);

  const addToCart = (product) => {
    setCart([...cart, product]);
  };

  return (
    <div>
      <Header cart={cart} />
      <Routes>
        <Route path="/" element={<ProductList products={products} addToCart={addToCart} />} />
        <Route path="/cart" element={<Cart cart={cart} />} />
        <Route path="/checkout" element={<Checkout />} />
      </Routes>
    </div>
  );
};

export default App;
